// SPDX-License-Identifier: GPL-3.0-only

pragma solidity ^0.8.18;

/**
 * @dev Contract module which provides a basic access control mechanism, where
 * there is an account (an owner) that can be granted exclusive access to
 * specific functions.
 *
 * By default, the owner account will be the one that deploys the contract. This
 * can later be changed with {transferOwnership}.
 *
 * This module is used through inheritance. It will make available the modifier
 * `onlyOwner`, which can be applied to your functions to restrict their use to
 * the owner.
 *
 * This adds a normal func that setOwner if _owner is address(0). So we can't allow
 * renounceOwnership. So we can support Proxy based upgradable contract
 */
abstract contract Ownable {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _setOwner(msg.sender);
    }

    /**
     * @dev Only to be called by inherit contracts, in their init func called by Proxy
     * we require _owner == address(0), which is only possible when it's a delegateCall
     * because constructor sets _owner in contract state.
     */
    function initOwner() internal {
        require(_owner == address(0), "owner already set");
        _setOwner(msg.sender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _setOwner(newOwner);
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contracts/safeguard/Pauser.sol.content:
// SPDX-License-Identifier: GPL-3.0-only

pragma solidity ^0.8.18;

import "@openzeppelin/contracts/security/Pausable.sol";
import "./Ownable.sol";

abstract contract Pauser is Ownable, Pausable {
    mapping(address => bool) public pausers;
    address[] public pauserList;

    event PauserAdded(address account);
    event PauserRemoved(address account);

    constructor() {
        _addPauser(msg.sender);
    }

    modifier onlyPauser() {
        require(isPauser(msg.sender), "Caller is not pauser");
        _;
    }

    function pause() public virtual onlyPauser {
        _pause();
    }

    function unpause() public virtual onlyPauser {
        _unpause();
    }

    function isPauser(address account) public view returns (bool) {
        return pausers[account];
    }

    function addPauser(address account) public onlyOwner {
        _addPauser(account);
    }

    function addPausers(address[] memory accounts) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _addPauser(accounts[i]);
        }
    }

    function removePauser(address account) public onlyOwner {
        _removePauser(account);
    }

    function removePausers(address[] memory accounts) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _removePauser(accounts[i]);
        }
    }

    function renouncePauser() public {
        _removePauser(msg.sender);
    }

    function numPausers() public view returns (uint256) {
        return pauserList.length;
    }

    function _addPauser(address account) private {
        require(!isPauser(account), "Account is already pauser");
        pauserList.push(account);
        pausers[account] = true;
        emit PauserAdded(account);
    }

    function _removePauser(address account) private {
        require(isPauser(account), "Account is not pauser");
        uint256 lastIndex = pauserList.length - 1;
        for (uint256 i = 0; i < pauserList.length; i++) {
            if (pauserList[i] == account) {
                if (i < lastIndex) {
                    pauserList[i] = pauserList[lastIndex];
                }
                pauserList.pop();
                pausers[account] = false;
                emit PauserRemoved(account);
                return;
            }
        }
        revert("pauser not found"); // this should never happen
    }
}

contracts/sdk/core/BrevisAggProof.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "../lib/Lib.sol";
import "../../interfaces/ISMT.sol";
import "../../verifiers/interfaces/IZkpVerifier.sol";
import "../../safeguard/BrevisAccess.sol";

contract BrevisAggProof is BrevisAccess {
    uint32 constant PUBLIC_BYTES_START_IDX = 12 * 32; // the first 12 32bytes are groth16 proof (A/B/C/Commitment/CommitmentPOK)
    uint8 constant TREE_DEPTH = 4;
    uint256 constant LEAF_NODES_LEN = 2 ** TREE_DEPTH;

    ISMT public smtContract;

    mapping(bytes32 => bool) public merkleRoots;
    mapping(uint64 => IZkpVerifier) public aggProofVerifierAddress;
    mapping(uint64 => bytes32) public dummyInputCommitments;
    event SmtContractUpdated(address smtContract);
    event AggProofVerifierAddressesUpdated(uint64[] chainIds, IZkpVerifier[] newAddresses);
    event DummyInputCommitmentsUpdated(uint64[] chainIds, bytes32[] updatedDummyInputCommitments);

    constructor(ISMT _smtContract) {
        smtContract = _smtContract;
    }

    /*********************************
     * External and Public Functions *
     *********************************/

    function submitAggProof(
        uint64 _chainId,
        bytes32[] calldata _proofIds,
        bytes calldata _proofWithPubInputs
    ) external onlyActiveProver {
        IZkpVerifier verifier = aggProofVerifierAddress[_chainId];
        require(address(verifier) != address(0), "chain agg proof verifier not set");
        require(verifier.verifyRaw(_proofWithPubInputs), "proof not valid");

        (bytes32 root, bytes32 proofIdsCommit) = unpack(_proofWithPubInputs);

        uint dataLen = _proofIds.length;
        bytes32[LEAF_NODES_LEN] memory rIds;
        for (uint i = 0; i < dataLen; i++) {
            rIds[i] = _proofIds[i];
        }
        // note, to align with circuit, rIds[dataLen] to rIds[LEAF_NODES_LEN - 1] filled with last real one
        if (dataLen < LEAF_NODES_LEN) {
            for (uint i = dataLen; i < LEAF_NODES_LEN; i++) {
                rIds[i] = rIds[dataLen - 1];
            }
        }
        require(keccak256(abi.encodePacked(rIds)) == proofIdsCommit, "proofIds not right");
        merkleRoots[root] = true;
    }

    // validate all leaf nodes in the agg proof data
    function validateAggProofData(uint64 _chainId, Brevis.ProofData[] calldata _proofDataArray) external view {
        uint dataLen = _proofDataArray.length;
        require(dataLen <= LEAF_NODES_LEN, "size exceeds");
        bytes32[2 * LEAF_NODES_LEN - 1] memory hashes;
        for (uint i = 0; i < dataLen; i++) {
            require(smtContract.isSmtRootValid(_chainId, _proofDataArray[i].smtRoot), "invalid smt root");
            require(dummyInputCommitments[_chainId] == _proofDataArray[i].dummyInputCommitment, "invalid dummy input");
            hashes[i] = keccak256(
                abi.encodePacked(
                    _proofDataArray[i].commitHash,
                    _proofDataArray[i].smtRoot,
                    _proofDataArray[i].appCommitHash,
                    _proofDataArray[i].appVkHash,
                    _proofDataArray[i].dummyInputCommitment
                )
            );
        }
        // note, hashes[dataLen] to hashes[LEAF_NODES_LEN - 1] filled with last real one
        if (dataLen < LEAF_NODES_LEN) {
            for (uint i = dataLen; i < LEAF_NODES_LEN; i++) {
                hashes[i] = hashes[dataLen - 1];
            }
        }

        uint shift = 0;
        uint counter = LEAF_NODES_LEN;
        while (counter > 0) {
            for (uint i = 0; i < counter - 1; i += 2) {
                hashes[shift + counter + i / 2] = keccak256(abi.encodePacked(hashes[shift + i], hashes[shift + i + 1]));
            }
            shift += counter;
            counter /= 2;
        }

        require(merkleRoots[hashes[hashes.length - 1]], "merkle root not exists");
    }

    // validate a single leaf node in the agg proof data
    function validateAggProofData(
        uint64 _chainId,
        Brevis.ProofData calldata _proofData,
        bytes32 _merkleRoot,
        bytes32[] calldata _merkleProof,
        uint8 _nodeIndex
    ) external view {
        require(merkleRoots[_merkleRoot], "merkle root not exists");
        require(smtContract.isSmtRootValid(_chainId, _proofData.smtRoot), "invalid smt root");
        require(dummyInputCommitments[_chainId] == _proofData.dummyInputCommitment, "invalid dummy input");

        bytes32 proofDataHash = keccak256(
            abi.encodePacked(
                _proofData.commitHash,
                _proofData.smtRoot,
                _proofData.appCommitHash,
                _proofData.appVkHash,
                _proofData.dummyInputCommitment
            )
        );
        bytes32 root = proofDataHash;
        for (uint8 depth = 0; depth < TREE_DEPTH; depth++) {
            if ((_nodeIndex >> depth) & 1 == 0) {
                root = keccak256(abi.encodePacked(root, _merkleProof[depth]));
            } else {
                root = keccak256(abi.encodePacked(_merkleProof[depth], root));
            }
        }
        require(_merkleRoot == root, "invalid data");
    }

    // -------- owner functions --------

    function updateSmtContract(ISMT _smtContract) public onlyOwner {
        smtContract = _smtContract;
        emit SmtContractUpdated(address(smtContract));
    }

    function updateAggProofVerifierAddresses(
        uint64[] calldata _chainIds,
        IZkpVerifier[] calldata _verifierAddresses
    ) public onlyOwner {
        require(_chainIds.length == _verifierAddresses.length, "length not match");
        for (uint256 i = 0; i < _chainIds.length; i++) {
            aggProofVerifierAddress[_chainIds[i]] = _verifierAddresses[i];
        }
        emit AggProofVerifierAddressesUpdated(_chainIds, _verifierAddresses);
    }

    function setDummyInputCommitments(
        uint64[] calldata _chainIds,
        bytes32[] calldata _dummyInputCommitments
    ) public onlyOwner {
        require(_chainIds.length == _dummyInputCommitments.length, "length not match");
        for (uint256 i = 0; i < _chainIds.length; i++) {
            dummyInputCommitments[_chainIds[i]] = _dummyInputCommitments[i];
        }
        emit DummyInputCommitmentsUpdated(_chainIds, _dummyInputCommitments);
    }

    /**********************************
     * Internal and Private Functions *
     **********************************/

    function unpack(
        bytes calldata _proofWithPubInputs
    ) internal pure returns (bytes32 merkleRoot, bytes32 proofIdsCommit) {
        merkleRoot = bytes32(_proofWithPubInputs[PUBLIC_BYTES_START_IDX:PUBLIC_BYTES_START_IDX + 32]);
        proofIdsCommit = bytes32(_proofWithPubInputs[PUBLIC_BYTES_START_IDX + 32:PUBLIC_BYTES_START_IDX + 2 * 32]);
    }
}

contracts/sdk/core/BrevisDispute.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./BrevisRequest.sol";
import "../interface/IBrevisDispute.sol";

// TODO: handle dispute fee
contract BrevisDispute is IBrevisDispute {
    BrevisRequest immutable brevisRequest;

    uint256 public challengeWindow;
    uint256 public responseTimeout;
    uint256 public depositAskForData;
    uint256 public depositAskForProof;

    mapping(bytes32 => Dispute) public disputes; // requestKey => Dispute

    modifier onlyActiveProver() {
        require(brevisRequest.isActiveProver(msg.sender), "invalid prover");
        _;
    }

    modifier onlyOwner() {
        require(brevisRequest.owner() == msg.sender, "invalid prover");
        _;
    }

    constructor(BrevisRequest _brevisRequest) {
        brevisRequest = _brevisRequest;
    }

    function askForRequestData(bytes32 _proofId, uint64 _nonce) external payable {
        require(msg.value > depositAskForData, "insufficient deposit");

        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        (RequestStatus status, uint64 timestamp, ) = brevisRequest.requests(requestKey);
        require(status == RequestStatus.OpSubmitted, "not in a disputable status");
        require(timestamp + challengeWindow > block.timestamp, "pass challenge window");
        brevisRequest.setRequestStatus(requestKey, RequestStatus.OpDisputing);

        Dispute storage dispute = disputes[requestKey];
        dispute.status = DisputeStatus.WaitingForRequestData;
        dispute.responseDeadline = block.timestamp + responseTimeout;
        dispute.challenger = msg.sender;
        dispute.deposit = msg.value;

        emit AskFor(_proofId, _nonce, DisputeStatus.WaitingForRequestData, msg.sender);
    }

    function postRequestData(
        bytes32 _proofId,
        uint64 _nonce,
        bytes[] calldata _data,
        uint256 _index,
        bool _done
    ) external onlyActiveProver {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        (RequestStatus status, , ) = brevisRequest.requests(requestKey);
        require(status == RequestStatus.OpDisputing, "invalid request status");

        Dispute storage dispute = disputes[requestKey];
        require(dispute.status == DisputeStatus.WaitingForRequestData, "invalid dispute status");
        RequestDataHash storage dataHash = dispute.requestDataHash;
        require(dataHash.hashes.length == _index, "invalid index");
        for (uint i = 0; i < _data.length; i++) {
            dataHash.hashes.push(keccak256(_data[i]));
        }
        if (_done) {
            dataHash.root = keccak256(abi.encodePacked(dataHash.hashes)); // todo: consider merkle
            disputes[requestKey].status = DisputeStatus.RequestDataPosted;
        }
        emit RequestDataPosted(_proofId, _nonce, _data, _index, _done);
    }

    function askForDataAvailabilityProof(bytes32 _proofId, uint64 _nonce) external payable {
        require(msg.value > depositAskForProof, "insufficient deposit");
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        (RequestStatus status, uint64 timestamp, ) = brevisRequest.requests(requestKey);
        require(timestamp + challengeWindow > block.timestamp, "pass challenge window");
        brevisRequest.setRequestStatus(requestKey, RequestStatus.OpDisputing);

        Dispute storage dispute = disputes[requestKey];
        require(
            status == RequestStatus.OpDisputing && dispute.status == DisputeStatus.RequestDataPosted,
            "invalid states"
        );

        dispute.status = DisputeStatus.WaitingForDataAvailabilityProof;
        dispute.responseDeadline = block.timestamp + responseTimeout;
        dispute.challenger = msg.sender;
        dispute.deposit = msg.value;

        emit AskFor(_proofId, _nonce, DisputeStatus.WaitingForDataAvailabilityProof, msg.sender);
    }

    function postDataAvailabilityProof(
        bytes32 _proofId,
        uint64 _nonce,
        bytes calldata // proof
    ) external onlyActiveProver {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        (RequestStatus status, , ) = brevisRequest.requests(requestKey);
        Dispute storage dispute = disputes[requestKey];
        require(
            status == RequestStatus.OpDisputing && dispute.status == DisputeStatus.WaitingForDataAvailabilityProof,
            "invalid states"
        );
        disputes[requestKey].status = DisputeStatus.DataAvailabilityProofPosted;
        // todo: validate proof

        emit DataAvailabilityProofPosted(_proofId, _nonce);
    }

    function askForDataValidityProof(bytes32 _proofId, uint64 _nonce) external payable {
        require(msg.value > depositAskForProof, "insufficient deposit");

        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        (RequestStatus status, uint64 timestamp, ) = brevisRequest.requests(requestKey);
        require(timestamp + challengeWindow > block.timestamp, "pass challenge window");
        brevisRequest.setRequestStatus(requestKey, RequestStatus.OpDisputing);

        Dispute storage dispute = disputes[requestKey];
        require(
            status == RequestStatus.OpSubmitted ||
                (status == RequestStatus.OpDisputing && dispute.status != DisputeStatus.WaitingForDataValidityProof),
            "invalid states"
        );

        dispute.status = DisputeStatus.WaitingForDataValidityProof;
        dispute.responseDeadline = block.timestamp + responseTimeout;
        dispute.challenger = msg.sender;
        dispute.deposit = msg.value;

        emit AskFor(_proofId, _nonce, DisputeStatus.WaitingForDataValidityProof, msg.sender);
    }

    function postDataValidityProof(
        bytes32 _proofId,
        uint64 _nonce,
        uint64 _chainId,
        bytes calldata _proof
    ) external onlyActiveProver {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        (RequestStatus status, , ) = brevisRequest.requests(requestKey);
        Dispute storage dispute = disputes[requestKey];
        require(
            status == RequestStatus.OpDisputing && dispute.status == DisputeStatus.WaitingForDataValidityProof,
            "invalid states"
        );

        (bytes32 proofId, bytes32 appCommitHash, bytes32 appVkHash) = brevisRequest.brevisProof().submitProof(
            _chainId,
            _proof
        );
        require(_proofId == proofId, "invalid proof: proofId");
        require(
            brevisRequest.opdata(requestKey) == keccak256(abi.encodePacked(appCommitHash, appVkHash)),
            "invalid proof: appHash"
        );
        brevisRequest.setRequestStatus(requestKey, RequestStatus.ZkAttested);
        dispute.status = DisputeStatus.DataValidityProofPosted;

        emit DataValidityProofProofPosted(_proofId, _nonce);
    }

    function getChallengeWindow() external view returns (uint256) {
        return challengeWindow;
    }

    function getDisputeStatus(bytes32 _requestKey) external view returns (DisputeStatus) {
        return disputes[_requestKey].status;
    }

    function getResponseDeadline(bytes32 _requestKey) external view returns (uint256) {
        return disputes[_requestKey].responseDeadline;
    }

    // --------------------- owner functions ---------------------

    function setChallengeWindow(uint256 _challengeWindow) external onlyOwner {
        uint256 oldChallengeWindow = challengeWindow;
        challengeWindow = _challengeWindow;
        emit ChallengeWindowUpdated(oldChallengeWindow, _challengeWindow);
    }

    function setResponseTimeout(uint256 _responseTimeout) external onlyOwner {
        uint256 oldResponseTimeout = responseTimeout;
        responseTimeout = _responseTimeout;
        emit ResponseTimeoutUpdated(oldResponseTimeout, _responseTimeout);
    }

    function setDisputeDeposits(uint256 _amtAskForData, uint256 _amtAskForProof) external onlyOwner {
        depositAskForData = _amtAskForData;
        depositAskForProof = _amtAskForProof;
        emit DisputeDepositsUpdated(_amtAskForData, _amtAskForProof);
    }
}

contracts/sdk/core/BrevisProof.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./BrevisAggProof.sol";
import "../lib/Lib.sol";
import "../../interfaces/ISMT.sol";
import "../../verifiers/interfaces/IZkpVerifier.sol";

contract BrevisProof is BrevisAggProof {
    mapping(uint64 => IZkpVerifier) public verifierAddresses; // chainid => snark verifier contract address
    mapping(bytes32 => bytes32) public proofs; // proofId => keccak256(abi.encodePacked(appCommitHash, appVkHash));
    event VerifierAddressesUpdated(uint64[] chainIds, IZkpVerifier[] newAddresses);

    constructor(ISMT _smtContract) BrevisAggProof(_smtContract) {}

    // To support upgradable deployment.
    // Can only be called once by Proxy via delegateCall, as initOwner will require _owner is 0.
    function init(ISMT _smtContract) external {
        initOwner();
        smtContract = _smtContract;
    }

    /*********************************
     * External and Public Functions *
     *********************************/

    function submitProof(
        uint64 _chainId,
        bytes calldata _proofWithPubInputs
    ) external onlyActiveProver returns (bytes32 proofId, bytes32 appCommitHash, bytes32 appVkHash) {
        require(verifyRaw(_chainId, _proofWithPubInputs), "proof not valid");
        Brevis.ProofData memory data = unpackProofData(_proofWithPubInputs);

        appCommitHash = data.appCommitHash;
        appVkHash = data.appVkHash;
        proofId = keccak256(abi.encodePacked(appVkHash, data.commitHash, appCommitHash));
        require(smtContract.isSmtRootValid(_chainId, data.smtRoot), "smt root not valid");
        require(dummyInputCommitments[_chainId] == data.dummyInputCommitment, "invalid dummy input");
        proofs[proofId] = keccak256(abi.encodePacked(appCommitHash, appVkHash));
    }

    function validateProofAppData(
        bytes32 _proofId,
        bytes32 _appCommitHash,
        bytes32 _appVkHash
    ) external view returns (bool) {
        require(proofs[_proofId] == keccak256(abi.encodePacked(_appCommitHash, _appVkHash)), "invalid data");
        return true;
    }

    // -------- owner functions --------

    function updateVerifierAddress(
        uint64[] calldata _chainIds,
        IZkpVerifier[] calldata _verifierAddresses
    ) public onlyOwner {
        require(_chainIds.length == _verifierAddresses.length, "length not match");
        for (uint256 i = 0; i < _chainIds.length; i++) {
            verifierAddresses[_chainIds[i]] = _verifierAddresses[i];
        }
        emit VerifierAddressesUpdated(_chainIds, _verifierAddresses);
    }

    /**********************************
     * Internal and Private Functions *
     **********************************/

    function unpackProofData(bytes calldata _proofWithPubInputs) internal pure returns (Brevis.ProofData memory data) {
        data.commitHash = bytes32(_proofWithPubInputs[PUBLIC_BYTES_START_IDX:PUBLIC_BYTES_START_IDX + 32]);
        data.smtRoot = bytes32(_proofWithPubInputs[PUBLIC_BYTES_START_IDX + 32:PUBLIC_BYTES_START_IDX + 2 * 32]);
        data.appCommitHash = bytes32(
            _proofWithPubInputs[PUBLIC_BYTES_START_IDX + 2 * 32:PUBLIC_BYTES_START_IDX + 3 * 32]
        );
        data.appVkHash = bytes32(_proofWithPubInputs[PUBLIC_BYTES_START_IDX + 3 * 32:PUBLIC_BYTES_START_IDX + 4 * 32]);
        data.dummyInputCommitment = bytes32(
            _proofWithPubInputs[PUBLIC_BYTES_START_IDX + 4 * 32:PUBLIC_BYTES_START_IDX + 5 * 32]
        );
    }

    function verifyRaw(uint64 _chainId, bytes calldata _proofWithPubInputs) private view returns (bool) {
        IZkpVerifier verifier = verifierAddresses[_chainId];
        require(address(verifier) != address(0), "chain verifier not set");
        return verifier.verifyRaw(_proofWithPubInputs);
    }
}

contracts/sdk/core/BrevisRequest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/utils/Strings.sol";

import "./FeeVault.sol";
import "../interface/IBrevisRequest.sol";
import "../interface/IBrevisProof.sol";
import "../interface/IBrevisDispute.sol";
import "../interface/IBrevisApp.sol";
import "../lib/Lib.sol";
import "../../safeguard/BrevisAccess.sol";
import "../../interfaces/ISigsVerifier.sol";
import "../../lib/Utils.sol";

contract BrevisRequest is IBrevisRequest, FeeVault, BrevisAccess {
    // common workflow
    IBrevisProof public brevisProof;
    uint256 public requestTimeout;
    mapping(bytes32 => Request) public requests; // requestKey => Request;
    mapping(bytes32 => OnchainRequestInfo) public onchainRequests; // requestKey => OnchainRequestInfo

    // optimistic workflow
    IBvnSigsVerifier public bvnSigsVerifier;
    IAvsSigsVerifier public avsSigsVerifier;
    IBrevisDispute public brevisDispute;
    string public baseDataURL;
    mapping(bytes32 => bytes32) public opdata; // requestKey => keccak256(abi.encodePacked(appCommitHash, appVkHash))

    uint8 constant OPT_IDX_SIG_BVN = 0;
    uint8 constant OPT_IDX_SIG_AVS = 1;

    constructor(
        address _feeCollector,
        IBrevisProof _brevisProof,
        IBvnSigsVerifier _bvnSigsVerifier
    ) FeeVault(_feeCollector) {
        brevisProof = _brevisProof;
        bvnSigsVerifier = _bvnSigsVerifier;
    }

    // To support upgradable deployment.
    // Can only be called once by Proxy via delegateCall, as initOwner will require _owner is 0.
    function init(
        address _feeCollector,
        IBrevisProof _brevisProof,
        IBvnSigsVerifier _bvnSigsVerifier,
        uint256 _requestTimeout
    ) external {
        initOwner();
        feeCollector = _feeCollector;
        brevisProof = _brevisProof;
        bvnSigsVerifier = _bvnSigsVerifier;
        requestTimeout = _requestTimeout;
    }

    /*********************************
     * External and Public Functions *
     *********************************/

    function sendRequest(
        bytes32 _proofId,
        uint64 _nonce,
        address _refundee,
        Callback calldata _callback,
        uint8 _option // bitmap 0: zk, 1: op bvn, 2: op avs, 3: op bvn and avs
    ) external payable {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        RequestStatus status = requests[requestKey].status;
        require(status == RequestStatus.Null, "invalid status");
        _option = _option & 0x03; // binary 00000011
        if (_option == 0) {
            status == RequestStatus.ZkPending;
        } else {
            status = RequestStatus.OpPending;
        }
        requests[requestKey] = Request(status, uint64(block.timestamp), _option);

        if (_refundee == address(0)) {
            _refundee = msg.sender;
        }
        bytes32 feeHash = keccak256(abi.encodePacked(msg.value, _refundee));
        onchainRequests[requestKey] = OnchainRequestInfo(feeHash, _callback);

        emit RequestSent(_proofId, _nonce, _refundee, msg.value, _callback, _option);
    }

    function fulfillRequest(
        bytes32 _proofId,
        uint64 _nonce,
        uint64 _chainId,
        bytes calldata _proof,
        bytes calldata _appCircuitOutput,
        address _callbackTarget
    ) external onlyActiveProver {
        (bytes32 proofId, bytes32 appCommitHash, bytes32 appVkHash) = brevisProof.submitProof(_chainId, _proof);
        require(_proofId == proofId, "invalid proofId");

        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        Request storage request = requests[requestKey];
        RequestStatus status = request.status;
        require(status == RequestStatus.ZkPending || status == RequestStatus.Null, "invalid status");
        request.status = RequestStatus.ZkAttested;

        if (_appCircuitOutput.length > 0) {
            require(appCommitHash == keccak256(_appCircuitOutput), "invalid circuit output");
        }
        bool success = _brevisCallback(_callbackTarget, appVkHash, _appCircuitOutput, requestKey, status);
        if (!success) {
            emit RequestCallbackFailed(_proofId, _nonce);
        }
        emit RequestFulfilled(_proofId, _nonce);
    }

    // fulfill batch requests with aggProof
    function fulfillRequests(
        bytes32[] calldata _proofIds,
        uint64[] calldata _nonces,
        uint64 _chainId,
        bytes calldata _proof,
        Brevis.ProofData[] calldata _proofDataArray,
        bytes[] calldata _appCircuitOutputs,
        address[] calldata _callbackTargets
    ) external onlyActiveProver {
        uint256 dataNum = _proofIds.length;
        brevisProof.submitAggProof(_chainId, _proofIds, _proof);
        brevisProof.validateAggProofData(_chainId, _proofDataArray);
        require(_callbackTargets.length == 1 || _callbackTargets.length == dataNum, "invalid callback length");

        uint256 numFulfilled;
        for (uint256 i = 0; i < dataNum; i++) {
            require(
                keccak256(
                    abi.encodePacked(
                        _proofDataArray[i].appVkHash,
                        _proofDataArray[i].commitHash,
                        _proofDataArray[i].appCommitHash
                    )
                ) == _proofIds[i],
                "invalid proofId"
            );
            require(_proofDataArray[i].appCommitHash == keccak256(_appCircuitOutputs[i]), "invalid circuit output");
            bytes32 requestKey = keccak256(abi.encodePacked(_proofIds[i], _nonces[i]));
            Request storage request = requests[requestKey];
            RequestStatus status = request.status;
            if (status == RequestStatus.ZkPending || status == RequestStatus.Null) {
                request.status = RequestStatus.ZkAttested;
                numFulfilled++;
                if (_callbackTargets.length > 1) {
                    bool success = _brevisCallback(
                        _callbackTargets[i],
                        _proofDataArray[i].appVkHash,
                        _appCircuitOutputs[i],
                        requestKey,
                        status
                    );
                    if (!success) {
                        emit RequestCallbackFailed(_proofIds[i], _nonces[i]);
                    }
                } else if (status == RequestStatus.ZkPending) {
                    Callback memory callback = onchainRequests[requestKey].callback;
                    require(callback.target == _callbackTargets[0], "callback mismatch");
                    require(callback.gas == 0, "invalid gas for batch callback");
                }
            }
        }
        require(numFulfilled > 0, "no fulfilled requests");
        if (_callbackTargets.length == 1) {
            bytes32[] memory appVkHashes = new bytes32[](_proofDataArray.length);
            for (uint256 i = 0; i < appVkHashes.length; i++) {
                appVkHashes[i] = _proofDataArray[i].appVkHash;
            }
            // If the call failed due any reason, anyone can trigger retry later by calling applyBrevisAggProof.
            (bool success, ) = _callbackTargets[0].call(
                abi.encodeWithSelector(IBrevisApp.brevisBatchCallback.selector, appVkHashes, _appCircuitOutputs)
            );
            if (!success) {
                emit RequestsCallbackFailed(_proofIds, _nonces);
            }
        }
        emit RequestsFulfilled(_proofIds, _nonces);
    }

    function increaseGasFee(
        bytes32 _proofId,
        uint64 _nonce,
        uint64 _addGas,
        uint256 _currentFee,
        address _refundee
    ) external payable {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        RequestStatus status = requests[requestKey].status;
        require(status == RequestStatus.ZkPending || status == RequestStatus.OpPending, "invalid status");

        OnchainRequestInfo storage info = onchainRequests[requestKey];
        require(info.feeHash == keccak256(abi.encodePacked(_currentFee, _refundee)), "invalid input");
        uint256 newFee = _currentFee + msg.value;
        info.feeHash == keccak256(abi.encodePacked(newFee, _refundee));
        if (_addGas > 0) {
            info.callback.gas += _addGas;
        }
        emit RequestFeeIncreased(_proofId, _nonce, info.callback.gas, newFee);
    }

    function refund(bytes32 _proofId, uint64 _nonce, uint256 _amount, address _refundee) external {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        Request memory request = requests[requestKey];
        RequestStatus status = request.status;
        require(status == RequestStatus.ZkPending || status == RequestStatus.OpPending, "invalid status");
        require(block.timestamp > request.timestamp + requestTimeout);

        bytes32 feeHash = onchainRequests[requestKey].feeHash;
        require(feeHash == keccak256(abi.encodePacked(_amount, _refundee)), "invalid input");
        (bool sent, ) = _refundee.call{value: _amount, gas: 50000}("");
        require(sent, "send native failed");
        requests[requestKey].status = RequestStatus.Refunded;
        emit RequestRefunded(_proofId, _nonce);
    }

    // --------------------- optimistic workflow functions ---------------------

    function fulfillOpRequests(
        bytes32[] calldata _proofIds,
        uint64[] calldata _nonces,
        bytes32[] calldata _appCommitHashes,
        bytes32[] calldata _appVkHashes,
        IBvnSigsVerifier.SigInfo calldata _bvnSigInfo,
        IAvsSigsVerifier.SigInfo calldata _avsSigInfo
    ) external onlyActiveProver {
        uint8 option = 0;
        require(_bvnSigInfo.sigs.length > 0 || _avsSigInfo.blockNum > 0, "empty sigs");
        bytes32 domain = keccak256(abi.encodePacked(block.chainid, address(this), "FulfillRequests"));
        bytes32 signedHash = keccak256(abi.encodePacked(domain, _proofIds, _nonces, _appCommitHashes, _appVkHashes));
        if (_bvnSigInfo.sigs.length > 0) {
            bvnSigsVerifier.verifySigs(signedHash, _bvnSigInfo.sigs, _bvnSigInfo.signers, _bvnSigInfo.powers);
            option = _bitSet(option, OPT_IDX_SIG_BVN);
        }
        if (_avsSigInfo.blockNum > 0) {
            avsSigsVerifier.verifySigs(signedHash, _avsSigInfo.blockNum, _avsSigInfo.params);
            option = _bitSet(option, OPT_IDX_SIG_AVS);
        }
        _submitOpStates(_proofIds, _nonces, _appCommitHashes, _appVkHashes, option); // to avoid "stack too deep"
        emit OpRequestsFulfilled(_proofIds, _nonces, _appCommitHashes, _appVkHashes);
    }

    function setRequestStatus(bytes32 requestKey, RequestStatus _status) external {
        require(msg.sender == address(brevisDispute), "invalid setter");
        requests[requestKey].status = _status;
    }

    // --------------------- app helper functions ---------------------

    // apply proved data to a target contract
    function applyBrevisProof(
        bytes32 _proofId,
        bytes32 _appVkHash,
        bytes32 _appCommitHash,
        bytes calldata _appCircuitOutput,
        address _callbackTarget
    ) external {
        brevisProof.validateProofAppData(_proofId, _appCommitHash, _appVkHash);
        require(_appCommitHash == keccak256(_appCircuitOutput), "invalid circuit output");
        IBrevisApp(_callbackTarget).brevisCallback(_appVkHash, _appCircuitOutput);
    }

    // apply multiple proved data fulfilled through AggProof to a target contract
    function applyBrevisAggProof(
        uint64 _chainId,
        Brevis.ProofData[] calldata _proofDataArray,
        bytes[] calldata _appCircuitOutputs,
        address[] calldata _callbackTargets
    ) external {
        uint256 dataNum = _proofDataArray.length;
        brevisProof.validateAggProofData(_chainId, _proofDataArray);
        for (uint256 i = 0; i < dataNum; i++) {
            if (_callbackTargets[i] != address(0)) {
                require(_proofDataArray[i].appCommitHash == keccak256(_appCircuitOutputs[i]), "invalid circuit output");
                (bool success, bytes memory res) = _callbackTargets[i].call(
                    abi.encodeWithSelector(
                        IBrevisApp.brevisCallback.selector,
                        _proofDataArray[i].appVkHash,
                        _appCircuitOutputs[i]
                    )
                );
                require(success, string.concat(Strings.toString(i), ":", Utils.getRevertMsg(res)));
            }
        }
    }

    // apply single proved data fulfilled through AggProof to a target contract
    function applyBrevisAggProof(
        uint64 _chainId,
        Brevis.ProofData calldata _proofData,
        bytes32 _merkleRoot,
        bytes32[] calldata _merkleProof,
        uint8 _nodeIndex,
        bytes calldata _appCircuitOutput,
        address _callbackTarget
    ) external {
        brevisProof.validateAggProofData(_chainId, _proofData, _merkleRoot, _merkleProof, _nodeIndex);
        require(_proofData.appCommitHash == keccak256(_appCircuitOutput), "invalid circuit output");
        IBrevisApp(_callbackTarget).brevisCallback(_proofData.appVkHash, _appCircuitOutput);
    }

    // --------------------- owner functions ---------------------

    function setRequestTimeout(uint256 _timeout) external onlyOwner {
        uint256 oldTimeout = requestTimeout;
        requestTimeout = _timeout;
        emit RequestTimeoutUpdated(oldTimeout, _timeout);
    }

    function setBaseDataURL(string memory _url) external onlyOwner {
        string memory oldURL = baseDataURL;
        baseDataURL = _url;
        emit BaseDataUrlUpdated(oldURL, _url);
    }

    function setBrevisProof(address _brevisProof) external onlyOwner {
        address oldAddr = address(brevisProof);
        brevisProof = IBrevisProof(_brevisProof);
        emit BrevisProofUpdated(oldAddr, _brevisProof);
    }

    function setBrevisDispute(address _brevisDispute) external onlyOwner {
        address oldAddr = address(brevisDispute);
        brevisDispute = IBrevisDispute(_brevisDispute);
        emit BrevisDisputeUpdated(oldAddr, _brevisDispute);
    }

    function setBvnSigsVerifier(address _bvnSigsVerifier) external onlyOwner {
        address oldAddr = address(bvnSigsVerifier);
        bvnSigsVerifier = IBvnSigsVerifier(_bvnSigsVerifier);
        emit BvnSigsVerifierUpdated(oldAddr, _bvnSigsVerifier);
    }

    function setAvsSigsVerifier(address _avsSigsVerifier) external onlyOwner {
        address oldAddr = address(avsSigsVerifier);
        avsSigsVerifier = IAvsSigsVerifier(_avsSigsVerifier);
        emit BvnSigsVerifierUpdated(oldAddr, _avsSigsVerifier);
    }

    // --------------------- view functions ---------------------

    function queryRequestStatus(bytes32 _proofId, uint64 _nonce) external view returns (RequestStatus, uint8) {
        return _queryRequestStatus(keccak256(abi.encodePacked(_proofId, _nonce)), brevisDispute.getChallengeWindow());
    }

    function queryRequestStatus(
        bytes32 _proofId,
        uint64 _nonce,
        uint256 _challengeWindow
    ) external view returns (RequestStatus, uint8) {
        return _queryRequestStatus(keccak256(abi.encodePacked(_proofId, _nonce)), _challengeWindow);
    }

    function validateOpAppData(
        bytes32 _proofId,
        uint64 _nonce,
        bytes32 _appCommitHash,
        bytes32 _appVkHash,
        uint8 _option
    ) external view returns (bool) {
        uint256 challengeWindow = brevisDispute.getChallengeWindow();
        return _validateOpAppData(_proofId, _nonce, _appCommitHash, _appVkHash, challengeWindow, _option);
    }

    function validateOpAppData(
        bytes32 _proofId,
        uint64 _nonce,
        bytes32 _appCommitHash,
        bytes32 _appVkHash,
        uint256 _challengeWindow,
        uint8 _option
    ) external view returns (bool) {
        return _validateOpAppData(_proofId, _nonce, _appCommitHash, _appVkHash, _challengeWindow, _option);
    }

    function validateOpAppData(
        bytes32[] calldata _proofIds,
        uint64[] calldata _nonces,
        bytes32[] calldata _appCommitHashes,
        bytes32[] calldata _appVkHashes,
        uint256 _challengeWindow,
        uint8 _option
    ) external view returns (bool) {
        for (uint256 i = 0; i < _proofIds.length; i++) {
            if (
                !_validateOpAppData(
                    _proofIds[i],
                    _nonces[i],
                    _appCommitHashes[i],
                    _appVkHashes[i],
                    _challengeWindow,
                    _option
                )
            ) {
                return false;
            }
        }
        return true;
    }

    function dataURL(bytes32 _proofId) external view returns (string memory) {
        if (bytes(baseDataURL).length == 0) {
            return "";
        }
        return string.concat(baseDataURL, Strings.toHexString(uint256(_proofId), 32));
    }

    /*********************
     * Private Functions *
     *********************/

    function _brevisCallback(
        address _callbackTarget,
        bytes32 _appVkHash,
        bytes calldata _appCircuitOutput,
        bytes32 _requestKey,
        RequestStatus _status
    ) private returns (bool) {
        uint256 gas;
        if (_status == RequestStatus.ZkPending /* is onchain request*/) {
            Callback memory callback = onchainRequests[_requestKey].callback;
            require(callback.target == _callbackTarget, "callback mismatch");
            gas = callback.gas;
        }
        if (_callbackTarget != address(0)) {
            if (gas == 0) {
                gas = gasleft();
            }
            // If the call failed due any reason, anyone can trigger retry later by calling applyBrevisProof.
            (bool success, ) = _callbackTarget.call{gas: gas}(
                abi.encodeWithSelector(IBrevisApp.brevisCallback.selector, _appVkHash, _appCircuitOutput)
            );
            if (!success) {
                return false;
            }
        }
        return true;
    }

    function _submitOpStates(
        bytes32[] calldata _proofIds,
        uint64[] calldata _nonces,
        bytes32[] calldata _appCommitHashes,
        bytes32[] calldata _appVkHashes,
        uint8 option
    ) private {
        uint64 timestamp = uint64(block.timestamp);
        for (uint i = 0; i < _proofIds.length; i++) {
            bytes32 requestKey = keccak256(abi.encodePacked(_proofIds[i], _nonces[i]));
            Request memory request = requests[requestKey];
            if (request.status == RequestStatus.OpPending) {
                require(request.option == option, "insufficient types of sigs");
            } else {
                require(request.status == RequestStatus.Null, "invalid status");
            }
            requests[requestKey] = Request(RequestStatus.OpSubmitted, timestamp, option);
            opdata[requestKey] = keccak256(abi.encodePacked(_appCommitHashes[i], _appVkHashes[i]));
        }
    }

    function _queryRequestStatus(
        bytes32 _requestKey, // keccak256(abi.encodePacked(_proofId, _nonce))
        uint256 _challengeWindow
    ) private view returns (RequestStatus, uint8) {
        Request memory request = requests[_requestKey];
        if (request.status == RequestStatus.OpSubmitted) {
            if (request.timestamp + _challengeWindow < block.timestamp) {
                return (RequestStatus.OpAttested, request.option);
            }
        } else if (request.status == RequestStatus.OpDisputing) {
            DisputeStatus dstatus = brevisDispute.getDisputeStatus(_requestKey);
            if (dstatus == DisputeStatus.RequestDataPosted || dstatus == DisputeStatus.DataAvailabilityProofPosted) {
                if (request.timestamp + _challengeWindow < block.timestamp) {
                    return (RequestStatus.OpAttested, request.option);
                }
            } else if (brevisDispute.getResponseDeadline(_requestKey) < block.timestamp) {
                // did not respond in time for WaitingForXXX
                return (RequestStatus.OpDisputed, request.option);
            }
        }
        return (request.status, request.option);
    }

    function _validateOpAppData(
        bytes32 _proofId,
        uint64 _nonce,
        bytes32 _appCommitHash,
        bytes32 _appVkHash,
        uint256 _challengeWindow,
        uint8 _option
    ) private view returns (bool readyToUse) {
        bytes32 requestKey = keccak256(abi.encodePacked(_proofId, _nonce));
        require(opdata[requestKey] == keccak256(abi.encodePacked(_appCommitHash, _appVkHash)), "invalid data");
        (RequestStatus status, uint8 option) = _queryRequestStatus(requestKey, _challengeWindow);
        if (status == RequestStatus.OpAttested) {
            return (_option & option) == _option;
        } else if (status == RequestStatus.ZkAttested) {
            return true;
        }
        return false;
    }

    function _bitSet(uint8 _value, uint8 _index) private pure returns (uint8) {
        uint8 mask = uint8(1 << _index);
        return _value | mask;
    }
}

contracts/sdk/core/FeeVault.sol.content:
// SPDX-License-Identifier: GPL-3.0-only

pragma solidity >=0.8.18;

import "../../safeguard/Ownable.sol";

/**
 * @title Allows the owner to set fee collector and allows fee collectors to collect fees
 */
contract FeeVault is Ownable {
    address public feeCollector;

    event FeeCollected(uint256 amount, address receiver);
    event FeeCollectorUpdated(address from, address to);

    constructor(address _feeCollector) {
        feeCollector = _feeCollector;
    }

    modifier onlyFeeCollector() {
        require(msg.sender == feeCollector, "not fee collector");
        _;
    }

    function collectFee(uint256 _amount, address _to) external onlyFeeCollector {
        (bool sent, ) = _to.call{value: _amount, gas: 50000}("");
        require(sent, "send native failed");
        emit FeeCollected(_amount, _to);
    }

    function setFeeCollector(address _feeCollector) external onlyOwner {
        address oldFeeCollector = feeCollector;
        feeCollector = _feeCollector;
        emit FeeCollectorUpdated(oldFeeCollector, _feeCollector);
    }

    receive() external payable {}
}

