// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "../../framework/SimpleReceiptApp.sol";
import "../../../lib/Lib.sol";
import "../../../interface/IBrevisProof.sol";

// Single receipt: Proof of liquidation
// Use Brevis to prove that an address has been liquidated in Compound v2.
// Specifically, prove there was a LiquidateBorrow event emitted by Compound v2 contract for an borrower address.

contract DemoLiquidationProof is SimpleReceiptApp {
    mapping(address => bool) public liquiationFlags;

    constructor(IBrevisProof _brevisProof) BrevisApp(_brevisProof) {}

    // should set correct FieldLocation in BrevisApp.setFieldLocations() to restrict
    // the first field address as from CompoundV2 and the topic as LiquidateBorrow
    function submitUserEvent(
        bytes32 _proofRequestId,
        uint64 _chainId,
        bytes32 _smtRoot,
        Brevis.ReceiptInfo calldata _info
    ) external {
        Brevis.ExtractInfos memory info;
        info.smtRoot = _smtRoot;
        info.receipts = new Brevis.ReceiptInfo[](1);
        info.receipts[0] = _info;

        validateRequest(_proofRequestId, _chainId, info);

        address borrower = address(bytes20(_info.logs[0].value));
        liquiationFlags[borrower] = true;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "../../framework/SimpleReceiptApp.sol";
import "../../../lib/Lib.sol";
import "../../../interface/IBrevisProof.sol";

// Multiple receipts: Aggregate trading volume in Uniswap pool
// Use Brevis to prove someone’s accumulative trading volume for a given pair in Uniswap.

contract DemoUniswapVolume is SimpleReceiptApp {
    mapping(address => uint256) public volumes;
    event SumVolume(address user, uint64 fromChain, uint256 volume);

    constructor(IBrevisProof _brevisProof) BrevisApp(_brevisProof) {}

    function submitUserVolume(
        bytes32 _proofRequestId,
        uint64 _chainId,
        bytes32 _smtRoot,
        Brevis.ReceiptInfo[] calldata _info
    ) external {
        Brevis.ExtractInfos memory info;
        info.smtRoot = _smtRoot;
        info.receipts = _info;
        validateRequest(_proofRequestId, _chainId, info);

        address swapper = address(bytes20(_info[0].logs[0].value));
        uint256 volume = abs(int256(uint256(_info[0].logs[1].value)));
        if (_info.length > 1) {
            for (uint256 i = 1; i < _info.length; i++) {
                require(swapper == address(bytes20(_info[i].logs[0].value)), "not a unique swapper");
                volume += abs(int256(uint256(_info[i].logs[1].value)));
            }
        }

        volumes[swapper] = volume;
        emit SumVolume(swapper, _chainId, volume);
    }

    function abs(int256 x) private pure returns (uint256) {
        return uint256(x >= 0 ? x : -x);
    }

    function getAttestedSwapSumVolume(address _swapper) external view returns (uint256) {
        return volumes[_swapper];
    }
}

contracts/sdk/apps/examples/slot/DemoAcc.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "../../framework/BrevisApp.sol";
import "../../../lib/Lib.sol";
import "../../../interface/IBrevisProof.sol";

// Single storage slot: Account age proof
// Use Brevis to prove the age of an address by proving its oldest storage slot.

contract DemoAccountAge is BrevisApp {
    mapping(address => uint64) public visibleSinceBlocks;

    constructor(IBrevisProof _brevisProof) BrevisApp(_brevisProof) {}

    function submitUserStorageInfo(
        bytes32 _proofRequestId,
        uint64 _chainId,
        bytes32 _smtRoot,
        Brevis.StorageInfo calldata _info
    ) external {
        Brevis.ExtractInfos memory info;
        info.smtRoot = _smtRoot;
        info.stores = new Brevis.StorageInfo[](1);
        info.stores[0] = _info;

        validateRequest(_proofRequestId, _chainId, info);

        visibleSinceBlocks[_info.account] = _info.blockNumber;
    }

    function getAccountAge(address _account) external view returns (uint64) {
        uint64 visibleSinceBlock = visibleSinceBlocks[_account];
        require(visibleSinceBlock > 0, "no proof yet");

        return uint64(block.number) - visibleSinceBlock; // if the proof is from another chain, then should get current block number from SMT
    }
}

contracts/sdk/apps/examples/slot/DemoTWA.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "../../framework/BrevisApp.sol";
import "../../../lib/Lib.sol";
import "../../../interface/IBrevisProof.sol";

// Multiple storage slots: TWAP over an interval in Uniswap
// Use Brevis to prove the time-weighted average price of the WETH-USDC pair over a given block range in Uniswap.

contract DemoTWAP is BrevisApp {
    struct Observation {
        // the block timestamp of the observation
        uint32 blockTimestamp;
        // the tick accumulator, i.e. tick * time elapsed since the pool was first initialized
        int56 tickCumulative;
        // the seconds per liquidity, i.e. seconds elapsed / max(1, liquidity) since the pool was first initialized
        uint160 secondsPerLiquidityCumulativeX128;
        // whether or not the observation is initialized
        bool initialized;
    }

    address public poolAddr; // Uniswap V3 WETH-USDC pool addr
    bytes32 public slotHash; // keccak hash of the index of the Observation slot

    constructor(IBrevisProof _brevisProof, address _poolAddr, bytes32 _slotHash) BrevisApp(_brevisProof) {
        poolAddr = _poolAddr;
        slotHash = _slotHash;
    }

    function submitWETHUSDCPoolStorageInfosAndReturnTWATick(
        bytes32 _proofRequestId,
        uint64 _chainId,
        bytes32 _smtRoot,
        Brevis.StorageInfo[] calldata _info
    ) external view returns (int56 twaTick) {
        require(_info.length == 2, "only needs two uniswap pool observations to calculate twa Tick");
        Brevis.ExtractInfos memory info;
        info.smtRoot = _smtRoot;
        info.stores = _info;
        for (uint256 i = 0; i < 2; i++) {
            require(_info[i].account == poolAddr, "not exepcted pool");
            require(_info[i].slot == slotHash, "not expected slot");
        }
        validateRequest(_proofRequestId, _chainId, info);

        require(_info[1].blockNumber > _info[0].blockNumber, "not right sequence");
        Observation memory o1 = unpackObs(uint256(_info[1].slotValue));
        Observation memory o0 = unpackObs(uint256(_info[0].slotValue));

        return (o1.tickCumulative - o1.tickCumulative) / int56(uint56(o1.blockTimestamp - o0.blockTimestamp));
    }

    function unpackObs(uint256 observation) private pure returns (Observation memory) {
        return
            Observation({
                blockTimestamp: uint32(observation),
                tickCumulative: int56(uint56(observation >> 32)),
                secondsPerLiquidityCumulativeX128: uint160(observation >> 88),
                initialized: true
            });
    }
}

